pipelineText:
[
  {
    $match:{
      schemaId: $id{Bullhorn Consultant Details}
    }
  },
  {
    $project: {
      id: 1,
      schemaId: $id{View Consultant Details},
      $id{View Consultant Details.Consultant}: {$toString:$$id{Bullhorn Consultant Details.Consultant}},
      $id{View Consultant Details.Country}: $$id{Bullhorn Consultant Details.Country}
    }
  },
  {
    $lookup:{
      from:"dataInstances",
      localField: $id{View Consultant Details.Consultant},
      foreignField: $id{Bullhorn Consultant Teams.Consultant},
      as: "TeamsLookup"
    } 
  },
  {
    $set: {
      "firstTeamsLookup": {$arrayElemAt: ["$TeamsLookup",0]}
    }
  },
  {
    $set: {
      $id{View Consultant Details.Team}: "$firstTeamsLookup._id{Bullhorn Consultant Teams.Team}",
      $id{View Consultant Details.Team Lead}: "$firstTeamsLookup._id{Bullhorn Consultant Teams.Team Lead}"
    }
  },
  {
    $sort: {
      $id{View Consultant Details.Consultant}: 1
    }
  }

]

[
  {
   $match: {
    schemaId: $id{TSP UK Invoice Tracker}
   }
  },
  {
   $project: {
    candidate: $$id{TSP UK Invoice Tracker.Candidate},
    invoiceDate: $$id{TSP UK Invoice Tracker.InvoiceDate},
    chargeCode: $$id{TSP UK Invoice Tracker.ChargeCode},
   }
  },
  {
   $unionWith: {
    coll: "dataInstances",
    pipeline: [
     {
      $match: {
       schemaId: $id{TSP UK Contractor Invoices Tracker}
      }
     },
     {
      $project: {
       candidate: $$id{TSP UK Contractor Invoices Tracker.Candidate},
       invoiceDate: $$id{TSP UK Contractor Invoices Tracker.InvoiceDate},
       chargeCode: $$id{TSP UK Contractor Invoices Tracker.ChargeCode},
      }
     }
    ]
   }
  },
  {
   $unionWith: {
    coll: "dataInstances",
    pipeline: [
     {
      $match: {
       schemaId: $id{Adjustments UK NFI}
      }
     },
     {
      $project: {
       candidate: $$id{Adjustments UK NFI.Candidate},
       invoiceDate: $$id{Adjustments UK NFI.InvoiceDate},
       chargeCode: $$id{Adjustments UK NFI.ChargeCode},
      }
     }
    ]
   }
  },
  {
   $set: {
    year: { $dateToString: { format: "%Y", date: { $toDate: "$invoiceDate" } } },
    month: { $concat: [{ $dateToString: { format: "%m", date: { $toDate: "$invoiceDate" } } }, "-", { $arrayElemAt: [["", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"], { $month: { $toDate: "$invoiceDate" } }] }] },
   }
  },
    /* Union with UK Placements Forecast */
  {   
      $unionWith: {
          coll: "dataInstances",
          pipeline: [
              {
                  $match: {
                  schemaId: $id{TSP UK Placements Forecast}
                  }
              }
              ,
              {
                  $project: {
                      candidate: $$id{TSP UK Placements Forecast.Candidate},
                      chargeCode: "Temp",
                      invoiceDate: $$id{TSP UK Placements Forecast.Forecast Date},
                  }
              }
              ,
              /* Common Calendar Columns */
              {
               $set: {
                year: { $dateToString: { format: "%Y", date: { $toDate: "$invoiceDate" } } },
                month: { $concat: [{ $dateToString: { format: "%m", date: { $toDate: "$invoiceDate" } } }, "-", { $arrayElemAt: [["", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"], { $month: { $toDate: "$invoiceDate" } }] }] },
               }
              }]
              }

  },
  /* END Union with UK Placements Forecast */

  /* ChargeCode Look Up */
  {
   $lookup: {
    from: "dataInstances",
    localField: "chargeCode",
    foreignField: $id{Metrics Charge Codes.ChargeCode},
    as: "types"
   }
  },
  {
   $set: {
    "firstType": { $arrayElemAt: ["$types", 0] }
   }
  },
  {
   $set: {
    type: "$firstType._id{Metrics Charge Codes.Type}"
   }
  },
  {
   $unset: ["types", "firstType"]
  },
  /* END */
  {
    $match:{
      type: "Contract"
    }
  },
  {
      $group: {
        _id: {
          year: "$year",
          month: "$month"
        },
        candidates: { $addToSet: "$candidate" }
      }
    },
    {
      $project: {
        id: "1",
        schemaId: $id{View Distinct Contractor Count UK},
        $id{View Distinct Contractor Count UK.Year}: "$_id.year",
        $id{View Distinct Contractor Count UK.Month}: "$_id.month",
        $id{View Distinct Contractor Count UK.Count}: {$size: "$candidates"},
      }
    }
]

