    {
      $project: {
        id: "1",
        schemaId: $id{View Distinct Contractor Count US},
        $id{View Distinct Contractor Count US.Year}: "$_id.year",
        $id{View Distinct Contractor Count US.Month}: "$_id.month",
        $id{View Distinct Contractor Count US.Count}: {$size: "$candidates"},
      }
    }
]

[
  {
    $match:
      {
        schemaId:
          $id{FOREX GBP}
      }
  },
  {
    $group:
      {
        _id: {
          year: $$id{FOREX GBP.Year},
          month: $$id{FOREX GBP.Month},
          day: $$id{FOREX GBP.Day},
          symbol: $$id{FOREX GBP.Symbol}
        },
        count: {
          $count: {}
        }
      }
  },
  {
    $match:{
      count: { $gt: 1 }
    }
  }
  ,
  {
    $project:
      {
        id: "1",
        schemaId: $id{View FOREX GBP Count},
        $id{View FOREX GBP Count.Year}: "$_id.year",
        $id{View FOREX GBP Count.Month}: "$_id.month",
        $id{View FOREX GBP Count.Day}: { $toInt: "$_id.day"},
        $id{View FOREX GBP Count.Symbol}: "$_id.symbol",
        $id{View FOREX GBP Count.Count}: "$count"
      }
  }
]

[
  {
    $match:
      {
        schemaId: $id{FOREX GBP}
      }
  }
  ,
  /* Create a yyyymmdd variable to sort by */
  {
    $set:{
      month_portion: {$substr: [$$id{FOREX GBP.Month}, 0, 2 ]}
    }
  }
  ,
  {
    $set:{
      day_portion:  { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX GBP.Day} }, "."] }, 0] }
    }
  }
  ,
  {
    $set:{
      day_padded: {$cond: {
                            if: { $gte: [{ $strLenCP: "$day_portion"}, 2] },
                            then: "$day_portion",
                            else: { $concat: ["0", "$day_portion"] }
                        }}
    }
  }
  ,
  {
    $set:{
      yyyymmdd: {  $concat: [ $$id{FOREX GBP.Year}, "$month_portion", "$day_padded"]  }
    }
  }
  /* END */
  ,
  {
    $sort:
      {
        "yyyymmdd": -1
      }
  },
  {
    $group:
      {
        _id: $$id{FOREX GBP.Symbol}
        , 
        rate: {
          $first: $$id{FOREX GBP.GBP Rate}
        }
        ,
        year: {
          $first: $$id{FOREX GBP.Year}
        }
        ,
        month: {
          $first: $$id{FOREX GBP.Month}
        }
        ,
        day: {
          $first: $$id{FOREX GBP.Day}
        }
      }
  }
  ,
  {
      $project: {
          id: "1",
          schemaId: $id{View FOREX GBP Forecast},
          $id{View FOREX GBP Forecast.Symbol}: "$_id",
          $id{View FOREX GBP Forecast.GBP Rate}: "$rate",
          $id{View FOREX GBP Forecast.Month}: "$month",
          $id{View FOREX GBP Forecast.Day}: "$day",
          $id{View FOREX GBP Forecast.Year}: "$year"

      }
  }
  ,
  {
    $unset: ["month_portion","day_portion","day_padded","yyyymmdd"]
  }
]

[

  { 
    $match: {
      $and:[
      {schemaId: $id{TSP UK Invoice Tracker}},
      {$id{TSP UK Invoice Tracker.Currency}: {$ne: "Â£"}}
      ]

    }
  },
  {
    $group: {
      _id: {
        invoicedate: $$id{TSP UK Invoice Tracker.InvoiceDate},
        symbol: $$id{TSP UK Invoice Tracker.Currency}
      }
    }
  },
  {
    $project: {
      id: "1",
      schemaId: $id{View FOREX GBP Missing},
      $id{View FOREX GBP Missing.InvoiceDate}: "$_id.invoicedate",
      $id{View FOREX GBP Missing.Symbol}: "$_id.symbol"
    }
  }
  ,
  {
     $unionWith: {
      coll: "dataInstances",
      pipeline: [
                { 
          $match: {
            $and:[
            {schemaId: $id{TSP UK Contractor Invoices Tracker}},
            {$id{TSP UK Contractor Invoices Tracker.Currency}: {$ne: "Â£"}}
            ]
          }
        },
        {
          $group: {
            _id: {
              invoicedate: $$id{TSP UK Contractor Invoices Tracker.InvoiceDate},
              symbol: $$id{TSP UK Contractor Invoices Tracker.Currency}
            }
          }
        },
        {
          $project: {
            id: "1",
            schemaId: $id{View FOREX GBP Missing},
            $id{View FOREX GBP Missing.InvoiceDate}: "$_id.invoicedate",
            $id{View FOREX GBP Missing.Symbol}: "$_id.symbol"
          }
        }
      ]
    }
  }
  ,

{
  $set:{
    $id{View FOREX GBP Missing.Year}: { $dateToString: { format: "%Y", date: { $toDate: $$id{View FOREX GBP Missing.InvoiceDate} } } },
    $id{View FOREX GBP Missing.Month}: { $concat: [{ $dateToString: { format: "%m", date: { $toDate: $$id{View FOREX GBP Missing.InvoiceDate} } } }, "-", { $arrayElemAt: [["", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"], { $month: { $toDate: $$id{View FOREX GBP Missing.InvoiceDate}} }] }] },
    $id{View FOREX GBP Missing.Day}: { $dayOfMonth: { $toDate: $$id{View FOREX GBP Missing.InvoiceDate} } }
  }
},
{
 $set: {
  fxLookup: {
   $concat: [
    { $toString: $$id{View FOREX GBP Missing.Year} },
    { $toString: $$id{View FOREX GBP Missing.Month} },
    { $toString: $$id{View FOREX GBP Missing.Day} },
    { $toString: $$id{View FOREX GBP Missing.Symbol} }
   ]
  }
 }
},
{
 $lookup: {
  from: "dataInstances",
  localField: "fxLookup",
  foreignField: "compoundIndexValues._id{FOREX GBP}.gbpFXLookup",
  as: "exchangeRates"
 }
},
{
 $match: {
  "exchangeRates": { $size: 0 }
 }
},
{
 $set: {
  "id": 1,
  "schemaId": $id{View FOREX GBP Missing}
 }
},
{
  $sort:{
    $id{View FOREX GBP Missing.Year}: 1,
    $id{View FOREX GBP Missing.Month}: 1,
    $id{View FOREX GBP Missing.Day}: 1,
    $id{View FOREX GBP Missing.Symbol}: 1
  }
}
]

