[
  $pipeline{View UK NFI Forecast},
  {
    $match: {
      $id{View UK NFI Forecast.Country}: "US"
    }
  },
  {
    $project: {
      id: 1,
      schemaId: $id{View Intercompany},
      $id{View Intercompany.Year}: $$id{View UK NFI Forecast.Year},
      $id{View Intercompany.Month}: $$id{View UK NFI Forecast.Month},
      $id{View Intercompany.GBP Amount}: $$id{View UK NFI Forecast.GBPLinePrice},
      $id{View Intercompany.Direction}: "Revenue Transfer to USA"
    }
  },
  {
    $unionWith: {
      coll: "dataInstances",
      pipeline: [
        {
          $match: {
            schemaId: $id{Adjustments Interco}
          }
        },
        {
          $project: {
            id: "1",
            schemaId: $id{View Intercompany},
            $id{View Intercompany.Year}: $$id{Adjustments Interco.Year},
            $id{View Intercompany.Month}: $$id{Adjustments Interco.Month},
            $id{View Intercompany.GBP Amount}: $$id{Adjustments Interco.GBP Amount},
            $id{View Intercompany.Direction}: "Revenue Transfer to USA"
          }
        }
      ]
    }
  },
  {
    $unionWith: {
      coll: "dataInstances",
      pipeline: [
        $pipeline{View US NFI Forecast},
        {
          $project: {
            id: 1,
            schemaId: $id{View Intercompany},
            $id{View Intercompany.Year}: $$id{View US NFI Forecast.Year},
            $id{View Intercompany.Month}: $$id{View US NFI Forecast.Month},
            $id{View Intercompany.GBP Amount}: $$id{View US NFI Forecast.GBPLinePrice},
            $id{View Intercompany.Direction}: "Revenue Transfer from USA"
          }
        }
      ]
    }
  }
]

[
  {
   $match: {
    schemaId: $id{TSP UK Invoice Tracker}
   }
  },
  {
   $project: {
    id: 1,
    schemaId: $id{View UK NFI Forecast},
    $id{View UK NFI Forecast.Candidate}: $$id{TSP UK Invoice Tracker.Candidate},
    $id{View UK NFI Forecast.CompanyName}: $$id{TSP UK Invoice Tracker.CompanyName},
    $id{View UK NFI Forecast.Placement}: { $toString: $$id{TSP UK Invoice Tracker.Placement} },
    $id{View UK NFI Forecast.InvoiceDate}: $$id{TSP UK Invoice Tracker.InvoiceDate},
    $id{View UK NFI Forecast.Currency}: $$id{TSP UK Invoice Tracker.Currency},
    $id{View UK NFI Forecast.LinePrice}: $$id{TSP UK Invoice Tracker.LinePrice},
    $id{View UK NFI Forecast.ChargeCode}: $$id{TSP UK Invoice Tracker.ChargeCode},
    $id{View UK NFI Forecast.Source}: $$id{TSP UK Invoice Tracker.Source},
    $id{View UK NFI Forecast.Country}: $$id{TSP UK Invoice Tracker.Country}
