  },
  {
   $set: {
    $id{View US NFI Forecast.Year}: { $dateToString: { format: "%Y", date: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } } },
    $id{View US NFI Forecast.Month}: { $concat: [{ $dateToString: { format: "%m", date: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } } }, "-", { $arrayElemAt: [["", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"], { $month: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } }] }] },
    $id{View US NFI Forecast.Day}: { $dayOfMonth: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } },
    $id{View US NFI Forecast.Quarter}: { $concat: ["Q", { $arrayElemAt: [["", "1", "1", "1", "2", "2", "2", "3", "3", "3", "4", "4", "4"], { $month: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } }] }] },
    $id{View US NFI Forecast.Multiplier}: { $cond: { if: { $eq: [$$id{View US NFI Forecast.Source}, "TSP US Contractor Invoices"] }, then: -1, else: 1 } }
   }
  },
  {
   $set: {
    fxLookup: {
     $concat: [
      { $toString: $$id{View US NFI Forecast.Year} },
      { $toString: $$id{View US NFI Forecast.Month} },
      { $toString: $$id{View US NFI Forecast.Day} },
      { $toString: $$id{View US NFI Forecast.Currency} }
     ]
    }
   }
  },
  {
   $lookup: {
    from: "dataInstances",
    localField: "fxLookup",
    foreignField: "compoundIndexValues._id{FOREX USD}.usdFXLookup",
    as: "exchangeRates"
   }
  },
  {
   $set: {
    "firstRate": { $arrayElemAt: ["$exchangeRates", 0] }
   }
  },
  {
   $set: {
    $id{View US NFI Forecast.ExchangeRate}: { $cond: [ { $eq: [$$id{View US NFI Forecast.Currency}, "USD"] } , 1, "$firstRate._id{FOREX USD.USD Rate}" ] }
   }
  },
  {
   $unset: ["exchangeRates", "firstRate", "fxLookup"]
  }
  ,
  {
   $set: {
    $id{View US NFI Forecast.USDLinePrice}: {
     $cond: {
      if: { $gt: [$$id{View US NFI Forecast.ExchangeRate}, 1] },
      then: { $multiply: [ $$id{View US NFI Forecast.Multiplier}, { $divide: [$$id{View US NFI Forecast.LinePrice}, $$id{View US NFI Forecast.ExchangeRate}] } ]},
      else: { $multiply: [ $$id{View US NFI Forecast.Multiplier}, { $multiply: [$$id{View US NFI Forecast.LinePrice}, $$id{View US NFI Forecast.ExchangeRate}] } ]}
     }
    }
   }
  }
  ,
  /* Bring in the Forecast date */
  {
    $lookup: {
      from: "dataInstances",
      pipeline: [
          {
              $match: {
              schemaId: $id{FOREX Forecast Date}
              }
          }
          ,
          {
              $project: {
              ForecastDate: $$id{FOREX Forecast Date.Forecast Date From}
              }
          }
          ,
          {
              $sort: {
              ForecastDate: -1
              }
          }
          ,
          {
              $limit: 1
          }  
      ]
      ,
      as: "forecast_date"
    }
  }
  ,
  {
      $set: { 
          "firstForecastDate" : { $arrayElemAt: ["$forecast_date",0]} 
      }
  }
  ,
  {
    $set: {
      $id{View US NFI Forecast.Forecast Cut Off}:"$firstForecastDate.ForecastDate"
    }
  }
  /* END */
  ,
  /* Union with US Placements Forecast */
  {   
      $unionWith: {
          coll: "dataInstances",
          pipeline: [
              {
                  $match: {
                  schemaId: $id{TSP US Placements Forecast}
                  }
              }
              ,
              {
                  $project: {
                      id: 1,
                      schemaId: $id{View US NFI Forecast},
                      $id{View US NFI Forecast.Candidate}: $$id{TSP US Placements Forecast.Candidate},
                      $id{View US NFI Forecast.ChargeCode}: "Temp",
                      $id{View US NFI Forecast.CompanyName}: $$id{TSP US Placements Forecast.CompanyName},
                      $id{View US NFI Forecast.Country}: "US",
                      $id{View US NFI Forecast.Placement}: { $toString: $$id{TSP US Placements Forecast.Placement} },
                      $id{View US NFI Forecast.InvoiceDate}: $$id{TSP US Placements Forecast.Forecast Date},
                      $id{View US NFI Forecast.Currency}: "n/a",
                      $id{View US NFI Forecast.LinePrice}: "0",
                      $id{View US NFI Forecast.ChargeRate}: $$id{TSP US Placements Forecast.Charge Rate},
                      $id{View US NFI Forecast.ChargeRateSymbol}: $$id{TSP US Placements Forecast.CC Desc},
                      $id{View US NFI Forecast.Source}: "US Placements Forecast",      
                      $id{View US NFI Forecast.Placement Start}: $$id{TSP US Placements Forecast.Placement Start},
                      $id{View US NFI Forecast.Placement End}: $$id{TSP US Placements Forecast.Placement End},
                      $id{View US NFI Forecast.PayRateSymbol}: $$id{TSP US Placements Forecast.PC Desc},
                      $id{View US NFI Forecast.PayRate}: $$id{TSP US Placements Forecast.Pay Rate},
                      $id{View US NFI Forecast.Unit}: $$id{TSP US Placements Forecast.Unit}
                  }
              }
              ,
              /* Common Calendar Columns */
              {
               $set: {
                $id{View US NFI Forecast.Year}: { $dateToString: { format: "%Y", date: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } } },
                $id{View US NFI Forecast.Month}: { $concat: [{ $dateToString: { format: "%m", date: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } } }, "-", { $arrayElemAt: [["", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"], { $month: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } }] }] },
                $id{View US NFI Forecast.Day}: { $dayOfMonth: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } },
                $id{View US NFI Forecast.Quarter}: { $concat: ["Q", { $arrayElemAt: [["", "1", "1", "1", "2", "2", "2", "3", "3", "3", "4", "4", "4"], { $month: { $toDate: $$id{View US NFI Forecast.InvoiceDate} } }] }] },
                $id{View US NFI Forecast.Multiplier}: { $cond: { if: { $eq: [$$id{View US NFI Forecast.Source}, "TSP US Contractor Invoices"] }, then: -1, else: 1 } }
               }
              }
              ,
              /* Bring in Forecast Cut Off Date */
              {
                $lookup: {
                  from: "dataInstances",
                  pipeline: [
                      {
                        $match: {
                          schemaId: $id{FOREX Forecast Date}
                        }
                      }
                      ,
                      {
                        $project: {
                          ForecastDate: $$id{FOREX Forecast Date.Forecast Date From}
                        }
                      }
                      ,
                      {
                        $sort: {
                          ForecastDate: -1
                        }
                      }
                      ,
                      {
                        $limit: 1
                      }  
                  ]
                  ,
                  as: "forecast_date"
                }
              }
              ,
              {
                  $set: { 
                      "firstForecastDate" : { $arrayElemAt: ["$forecast_date",0]} 
                  }
              }
              ,
              {
                $set: {
                  $id{View US NFI Forecast.Forecast Cut Off}:"$firstForecastDate.ForecastDate"
                }
              }
              ,
              /* END */
              /* Charge Rate FX lookup */
              {
                  $lookup: {
                      from: "dataInstances",
                      localField: $id{View US NFI Forecast.ChargeRateSymbol},
                      foreignField: $id{FOREX USD.Symbol},
                      pipeline: [
                        {
                          $sort: {
                            $id{FOREX GBP.Year}: -1, $id{FOREX GBP.Month}: -1, $id{FOREX GBP.Day}: -1
                          }
                        },
                        {
                          $limit: 1
                        },

                                  {
                                      $project:{

                                      yyyymmdd: { 

                                      $concat: [ $$id{FOREX USD.Year}, {$substr: [$$id{FOREX USD.Month}, 0, 2 ]}, {$cond: {
                                        if: { $gte: [{ $strLenCP: { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] }}, 2] },
                                        then: { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] },
                                        else: { $concat: ["0", { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] }] }
                                    }}]  


                                    },
                                    $id{FOREX USD.USD Rate}:1
                                      }
                                  }
                                  /* END */

                          ],
                      as: "ForecastChargeExchangeRates"
                  }
              }
              ,
              {
                  $set: { 
                      "firstChargeExchangeRates" : { $arrayElemAt: ["$ForecastChargeExchangeRates",0]} 
                  }
              },
              {
                $unset: "ForecastChargeExchangeRates"
              }
              ,
              {
                  $set:{
                          $id{View US NFI Forecast.ChargeRateExchange}: { 
                          $cond: [ { $eq: [$$id{View US NFI Forecast.ChargeRateSymbol}, "USD"] } , 1, "$firstChargeExchangeRates._id{FOREX USD.USD Rate}" ]
                      }
                  }
              }
              ,
              {
                  $unset: [ "firstChargeExchangeRates"]
              }
              ,
              {
                  $set: {
                      $id{View US NFI Forecast.ChargeRateUSD}: {
                          $cond: {
                              if: { $gt: [$$id{View US NFI Forecast.ChargeRateExchange}, 1] },
                              then: { $divide: [$$id{View US NFI Forecast.ChargeRate},$$id{View US NFI Forecast.ChargeRateExchange}] },
                              else: { $multiply: [$$id{View US NFI Forecast.ChargeRate},$$id{View US NFI Forecast.ChargeRateExchange}]}
                          }
                      }
                  }
              }
              ,
              {
                  $set:{
                      $id{View US NFI Forecast.ChargeRateUSD}: {
                          $round: [{$toDouble:$$id{View US NFI Forecast.ChargeRateUSD}},2]

                      }
                  }
              }
              /* END */
              ,
              /* Pay Rate FX Lookup */
              {
                  $lookup: {
                      from: "dataInstances",
                      localField: $id{View US NFI Forecast.PayRateSymbol},
                      foreignField: $id{FOREX USD.Symbol},
                      pipeline: [

                        {
                          $project:{

                          yyyymmdd: { 

                          $concat: [ $$id{FOREX USD.Year}, {$substr: [$$id{FOREX USD.Month}, 0, 2 ]}, {$cond: {
                            if: { $gte: [{ $strLenCP: { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] }}, 2] },
                            then: { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] },
                            else: { $concat: ["0", { $arrayElemAt: [{ $split: [{ $toString: $$id{FOREX USD.Day} }, "."] }, 0] }] }
                        }}]  


                        },
                        $id{FOREX USD.USD Rate}:1
                          }
                      }
                      /* END */
                      ,
                      {
                          $sort:
                          {
                              "yyyymmdd": -1
                          }
                      },
                      {
                          $limit:1
                      }
                          ],
                      as: "ForecastPayExchangeRates"
                  }
              },
              {
                  $set: { 
                      "firstPayExchangeRates" : { $arrayElemAt: ["$ForecastPayExchangeRates",0]} 
                  }
              },
              {
                $unset: "ForecastPayExchangeRates"
              },
              {
                  $set:{
                      $id{View US NFI Forecast.PayRateExchange}: { 
                          $cond: [ { $eq: [$$id{View US NFI Forecast.PayRateSymbol}, "USD"] } , 1, "$firstPayExchangeRates._id{FOREX USD.USD Rate}" ]
                      }
                  }
              },
              {
                  $unset: ["firstPayExchangeRates"]
              }
              ,
              {
                  $set: {
                      $id{View US NFI Forecast.PayRateUSD}: {
                          $cond: {
                              if: { $gt: [$$id{View US NFI Forecast.PayRateExchange}, 1] },
                              then: { $divide: [$$id{View US NFI Forecast.PayRate},$$id{View US NFI Forecast.PayRateExchange}] },
                              else: { $multiply: [$$id{View US NFI Forecast.PayRate},$$id{View US NFI Forecast.PayRateExchange}] }
                          }
                      }
                  }
              },
              {
                  $set:{
                      $id{View US NFI Forecast.PayRateUSD}: {
                      $round: [{$toDouble:$$id{View US NFI Forecast.PayRateUSD}},2]
                      }
                  }
              }
              /* END */
              ,

              /* Working Days Lookup */
              {
                  $set: {
                      wdLookup:{
                      $concat: [
                          "US",
                          {$toString: $$id{View US NFI Forecast.Year} },
                          {$toString: $$id{View US NFI Forecast.Month} }
                      ] 
                      }
                  }
              },
              {
                  $lookup:{
                      from: "dataInstances",
                      localField: "wdLookup",
                      foreignField: "compoundIndexValues._id{Metrics Working Days}.workingDaysLookup",
                      as: "workingDays"
                  }
              },
              {
                  $set: {"firstWorkingDays":{ $arrayElemAt: ["$workingDays",0]}}
              },
              {
                  $set : { $id{View US NFI Forecast.WorkingDays}: "$firstWorkingDays._id{Metrics Working Days.Nos Days}" }
              },
              {
                  $unset: ["workingDays", "firstWorkingDays", "wdLookup"]
              }
              /* END */
              ,

              /* Calculate Net Revenue and final forecast USD based on working days */
              {
                  $set: {
                      $id{View US NFI Forecast.NetRevenueUSD}: {
                      $subtract:[ $$id{View US NFI Forecast.ChargeRateUSD}, $$id{View US NFI Forecast.PayRateUSD} ]
                      }
                  }
              }
              ,
              {
                  $set:{
                      $id{View US NFI Forecast.NetRevenueUSD}: {
                      $round: [{$toDouble:$$id{View US NFI Forecast.NetRevenueUSD}},2]
                      }
                  }
              }
              ,
              {
                  $set:{
                          $id{View US NFI Forecast.USDLinePrice}: 
                          {
                              $cond: {
                                          if: {$eq: [ $$id{View US NFI Forecast.Unit}, "Per Day"]},
                                          then: 
                                          {
                                              $multiply: [{$toDouble:$$id{View US NFI Forecast.WorkingDays}}, {$toDouble:$$id{View US NFI Forecast.NetRevenueUSD}}]
                                          },
                                          else: $$id{View US NFI Forecast.USDLinePrice}
                                      }
                          }
                  }
              }
                  ,
              {
                  $set:{
                      $id{View US NFI Forecast.USDLinePrice}: 
                          {
                              $cond: {
                                          if: {$eq: [ $$id{View US NFI Forecast.Unit}, "Per Hour"]},
                                          then: 
                                          {
                                              $multiply: [7.5,{$toDouble:$$id{View US NFI Forecast.WorkingDays}}, {$toDouble:$$id{View US NFI Forecast.NetRevenueUSD}}]
                                          },
                                          else: $$id{View US NFI Forecast.USDLinePrice}
                                      }
                                  }
                  }
              }
              ,
              {
                  $set:{
                      $id{View US NFI Forecast.USDLinePrice}:
                      {
                          $round: [{$toDouble:$$id{View US NFI Forecast.USDLinePrice}},2]
                      }
                  }
              }
              /* END */
              ,
              {
                  $set: { 
                      "firstForecastDate" : { $arrayElemAt: ["$forecast_date",0]} 
                  }
              }
              ,
              {
                  $set: {
                      $id{View US NFI Forecast.Forecast Cut Off}:"$firstForecastDate.ForecastDate"
                  }
              }
              /* END */
              ,
              {
                  $set:{
                      $id{View US NFI Forecast.NetRevenueUSD}: { $cond: { if: { $lte: [$$id{View US NFI Forecast.InvoiceDate}, $$id{View US NFI Forecast.Forecast Cut Off}] }, then: 0, else: $$id{View US NFI Forecast.NetRevenueUSD} } }
                  }

                }
                ,
                {
                  $set:{
                      $id{View US NFI Forecast.USDLinePrice}: { $cond: { if: { $lte: [$$id{View US NFI Forecast.InvoiceDate}, $$id{View US NFI Forecast.Forecast Cut Off}] }, then: 0, else: $$id{View US NFI Forecast.USDLinePrice} } }
                  }

                }
          ]
      }
  }
  /* END Union with US Placements Forecast */
  ,
  /* Consultant Look Up */
  {
   $set: {
    consultantLookup: {
     $concat: [
      { $toString: $$id{View US NFI Forecast.Placement} },
      { $toString: $$id{View US NFI Forecast.Candidate} }
     ]
    }
   }
  },
  {
   $lookup: {
    from: "dataInstances",
    localField: "consultantLookup",
    foreignField: "compoundIndexValues._id{TSP US Placements Tracker}.placementCandidateLookup",
    as: "consultants"
   }
  },
  {
   $set: {
    "firstConsultant": { $arrayElemAt: ["$consultants", 0] }
   }
  },
  {
   $set: {
    $id{View US NFI Forecast.Consultant}: "$firstConsultant._id{TSP US Placements Tracker.Consultant}"
   }
  },
  {
   $unset: ["consultants", "firstConsultant", "consultantLookup"]
  }
  /* END */
  ,
  /* ChargeCode Look Up */
  {
   $lookup: {
    from: "dataInstances",
    localField: $id{View US NFI Forecast.ChargeCode},
    foreignField: $id{Metrics Charge Codes.ChargeCode},
    as: "types"
   }
  },
  {
   $set: {
    "firstType": { $arrayElemAt: ["$types", 0] }
   }
  },
  {
   $set: {
    $id{View US NFI Forecast.Type}: "$firstType._id{Metrics Charge Codes.Type}"
   }
  },
  {
   $unset: ["types", "firstType"]
  }
  /* END */
  ,
  /* Add Consultant Team and Country Lookup */
  {
    $lookup: {
      from: $id{View Consultant Details},
      localField: $id{View US NFI Forecast.Consultant},
      foreignField: $id{View Consultant Details.Consultant},
      as: "consultantDetails"
    }
  },
  {
    $set: {
      "firstConsultantDetails": {$arrayElemAt: ["$consultantDetails",0]}
    }
  },
  {
    $set: {
      $id{View US NFI Forecast.Team}: "$firstConsultantDetails._id{View Consultant Details.Team}",
      $id{View US NFI Forecast.Team Lead}: "$firstConsultantDetails._id{View Consultant Details.Team Lead}",
      $id{View US NFI Forecast.Country}: "$firstConsultantDetails._id{View Consultant Details.Country}"
    }
  }

]
